import { StatusBar } from 'expo-status-bar';
import React from 'react';
import { Button, 
    Keyboard,
    KeyboardAvoidingView, 
    Platform,
    StyleSheet, 
    Text, 
    TouchableWithoutFeedback, 
    TouchableOpacity, 
    TextInput, 
    View, } from 'react-native';



export default function App() {
  
  return (
    <KeyboardAvoidingView 
      behavior = {Platform.OS == 'ios' ? 'padding' : 'height'}
      style={styles.container}>

      <View style={styles.container}>
        <Text style = {styles.title}>
           Water Controls
        </Text>
        <Text style={styles.subTitle}>Flow</Text>
        <View style={styles.button}>
          <Button title = "Start Water Flow"  onPress={() => alert('Water Flow has begun')}/>
        </View>
        <View style={styles.button}>
          <Button title  = "End Water Flow"  onPress={() => alert('Water Flow has stopped')}/>
        </View>
      {/*
      <TouchableOpacity onPress={() => alert('Water Flow has begun')} style={styles.button}>
        <Text style={styles.buttonText}>Start Water Flow</Text>
      </TouchableOpacity>
      
      <TouchableOpacity onPress={() => alert('Water Flow has stopped')} style={styles.button}>
        <Text style={styles.buttonText}>End Water Flow</Text>
      </TouchableOpacity>
      */}
        <Text style={styles.subTitle}>Timer</Text>
      
      
        <StatusBar style="auto" />
      </View>

    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#e6efbf',
    //padding: 24,
    //alignItems: 'center',
    //justifyContent: 'center',
  },
  title: {
    //marginTop: 16,
    marginBottom: 20,
    paddingVertical: 60,
    //borderWidth: 4,
    //borderColor: '#20232a',
    backgroundColor: '#4ac3be',
    color: '#e6efbf',
    textAlign: 'left',
    //position: 'absolute',
    //width: 269,
    //height: 54,
    //left: 26,
   // top: 108,
    fontSize: 48, 
    fontWeight: 'normal',
  },
  subTitle: {
    color: '#4ac3be',
    fontSize: 36,
    marginHorizontal: 20,
    marginBottom: 20,
  },
  button: {
    backgroundColor: '#a3dec9',
    //padding: 20,
    width: 200,
    borderRadius: 15,
    marginBottom: 20,
  },
  buttonText: {
    fontSize: 20,
    color: '#fff',
  },
});
